pragma solidity 0.8.30;

import "./hyperlane/HypNative.sol";

contract EspHypNative is HypNative {
    uint8 public constant VERSION = 2;

    // The NFT sale price in Wei
    uint256 public nftSalePrice;

    // The Hyperlane domain ID of the destination chain.
    uint32 public destinationDomainId;

    event NftSalePriceSet(uint256 price);
    event DestinationDomainIdSet(uint32 domainId);

    error UseInitiateCrossChainNftPurchaseFunction();
    error NftPriceExceedsMsgValue(uint256 nftPrice, uint256 msgValue);

    constructor(uint256 _scale, address _mailbox) HypNative(_scale, _mailbox) {
        _disableInitializers;
    }

    function initializeV2(uint256 _nftSalePrice, uint32 _destinationDomainId) external reinitializer(VERSION) {
        nftSalePrice = _nftSalePrice;
        emit NftSalePriceSet(_nftSalePrice);

        destinationDomainId = _destinationDomainId;
        emit DestinationDomainIdSet(_destinationDomainId);
    }

    function transferRemote(uint32, bytes32, uint256) external payable override returns (bytes32) {
        revert UseInitiateCrossChainNftPurchaseFunction();
    }

    function transferRemote(uint32, bytes32, uint256, bytes calldata, address)
        external
        payable
        override
        returns (bytes32)
    {
        revert UseInitiateCrossChainNftPurchaseFunction();
    }

    function _transferFromSender(uint256) internal view virtual override returns (bytes memory) {
        return bytes(""); // no metadata
    }

    /**
     * @dev Entry point for a cross-chain NFT purchase.
     *     NOTE: `msg.value` will be greater than `nftSalePrice`, since it includes funds to cover cross-chain gas payment.
     *        The post-dispatch IGP hook handles cross-chain gas payment errors, so there is no need to check here if the user has supplied
     *        sufficient cross-chain gas funds.
     *     @param _recipient The address of the recipient on the destination chain; this MUST be the user's address on the destination chain.
     */
    function initiateCrossChainNftPurchase(bytes32 _recipient) external payable returns (bytes32 messageId) {
        if (msg.value < nftSalePrice) revert NftPriceExceedsMsgValue(nftSalePrice, msg.value);

        uint256 hookPayment = msg.value - nftSalePrice;

        return _transferRemote(destinationDomainId, _recipient, nftSalePrice, hookPayment);
    }
}

src/EspNFT.sol.content:
pragma solidity 0.8.30;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title EspNFT  ERC721 with role-restricted minting, base-IPFS tokenImageUri generation and onchain metadata.
 * @notice Minting restricted to MINTER_ROLE.
 * @dev Uses OpenZeppelin ERC721 + AccessControl
 */
contract EspNFT is ERC721, AccessControl {
    using Strings for uint256;
    using Strings for string;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    // The NFT sale price in Wei
    uint256 public nftSalePrice;
    address payable public treasury;

    uint256 public lastTokenId;
    string private baseImageURI;
    string private chainName;
    mapping(uint256 tokenId => uint256 machineType) public machineTypes;

    event TokenMinted(address indexed to, uint256 indexed tokenId, uint256 machineType);
    event BaseImageUriChanged(string oldBaseImageUri, string newBaseImageUri);
    event NftSalePriceSet(uint256 price);
    event TreasurySet(address treasuryAddress);
    event NativeBuy(address to, uint256 tokenId, uint256 price);

    error NftPriceExceedsMsgValue(uint256 nftPrice, uint256 msgValue);
    error UriQueryNotExist(uint256 tokenId);
    error CallerIsNotAnTokenOwnerOrApproved(address caller, uint256 tokenId);
    error TreasuryPaymentFailed();
    error ZeroAddress();

    constructor(
        string memory _name,
        string memory _symbol,
        string memory _baseImageURI,
        string memory _chainName,
        address _espHypErc20,
        address payable _treasury,
        uint256 _nftSalePrice
    ) ERC721(_name, _symbol) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, _espHypErc20);

        baseImageURI = _baseImageURI;
        chainName = _chainName;
        _setTreasuryAndPrice(_treasury, _nftSalePrice);
    }

    /**
     * @notice Mint token on 'to' address
     * If caller is not EspHypERC20 (not xchain mint) we charge caller
     * to pay for NFT in native currency in the same chain
     * @dev Only accounts with MINTER_ROLE can call without native currency payment.
     */
    function mint(address to) external payable {
        bool xChainMint = hasRole(MINTER_ROLE, msg.sender);
        uint256 tokenId = lastTokenId++;

        if (!xChainMint) _nativeBuy(to, tokenId);
        uint256 machineType = _generateMachineType(tokenId);

        _safeMint(to, tokenId);
        emit TokenMinted(to, tokenId, machineType);
    }

    /**
     * @notice Set base image URI (e.g. "ipfs://Qm.../")  imageUrl will be baseImageURI + machineTheme + .png
     * @dev Only DEFAULT_ADMIN_ROLE can change
     */
    function setBaseImageUri(string calldata newImageURI) external onlyRole(DEFAULT_ADMIN_ROLE) {
        string memory old = baseImageURI;
        baseImageURI = newImageURI;
        emit BaseImageUriChanged(old, baseImageURI);
    }

    function setTreasuryAndPrice(address payable _treasury, uint256 _nftSalePrice)
        external
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        _setTreasuryAndPrice(_treasury, _nftSalePrice);
    }

    function _setTreasuryAndPrice(address payable _treasury, uint256 _nftSalePrice) internal {
        if (_treasury == address(0)) revert ZeroAddress();
        treasury = _treasury;
        emit TreasurySet(_treasury);

        nftSalePrice = _nftSalePrice;
        emit NftSalePriceSet(_nftSalePrice);
    }

    /**
     * @notice tokenURI returns composed metadata json NFT.
     * ===========================
     * tokenURI management and generation
     * ===========================
     */
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        if (!_exists(tokenId)) revert UriQueryNotExist(tokenId);

        if (bytes(baseImageURI).length == 0) {
            return string(abi.encodePacked(tokenId.toString()));
        }

        string memory machineTheme = _getMachineTheme(tokenId);

        // Image URL example "ipfs://img123abc/Future.png"
        string memory imageURL = string(abi.encodePacked(baseImageURI, machineTheme, ".png"));

        // Compose the metadata JSON
        string memory json = string(
            abi.encodePacked(
                "{",
                '"name": "',
                chainName,
                " Espresso Machine #",
                tokenId.toString(),
                '",',
                '"description": "Mint across chains without bridging. Powered by Espresso, ApeChain, and RARI Chain to showcase seamless, composable NFT minting.",',
                '"image": "',
                imageURL,
                '",',
                '"attributes": [',
                '{ "trait_type": "Theme", "value": "',
                machineTheme,
                '" }',
                "]",
                "}"
            )
        );

        // Encode JSON to base64 for full ERC721 compliance
        return string(abi.encodePacked("data:application/json;utf8,", json));
    }

    // @notice Burn token (owner or approved)
    function burn(uint256 tokenId) external {
        if (!_isApprovedOrOwner(msg.sender, tokenId)) revert CallerIsNotAnTokenOwnerOrApproved(msg.sender, tokenId);
        _burn(tokenId);
    }

    function _generateMachineType(uint256 tokenId) internal returns (uint256) {
        uint8 machineType = uint8((uint256(keccak256(abi.encodePacked(block.timestamp, msg.sender, tokenId))) % 5) + 1);
        machineTypes[tokenId] = machineType;
        return machineType;
    }

    function _nativeBuy(address to, uint256 tokenId) internal {
        if (msg.value != nftSalePrice) revert NftPriceExceedsMsgValue(nftSalePrice, msg.value);

        (bool success,) = treasury.call{value: nftSalePrice}("");
        if (!success) revert TreasuryPaymentFailed();
        emit NativeBuy(to, tokenId, nftSalePrice);
    }

    function _getMachineTheme(uint256 tokenId) internal view returns (string memory) {
        uint256 machineType = machineTypes[tokenId];
        if (machineType == 1) {
            return "Future";
        } else if (machineType == 2) {
            return "Classic";
        } else if (machineType == 3) {
            return "Industrial";
        } else if (machineType == 4) {
            return "Organic";
        } else {
            return "Mythic";
        }
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

src/hyperlane/HypNative.sol.content:
// SPDX-License-Identifier: Apache-2.0
// Source https://github.com/hyperlane-xyz/hyperlane-monorepo/blob/main/solidity/contracts/token/HypNative.sol
pragma solidity >=0.8.0;

import {TokenRouter} from "@hyperlane-core/solidity/contracts/token/libs/TokenRouter.sol";
import {FungibleTokenRouter} from "@hyperlane-core/solidity/contracts/token/libs/FungibleTokenRouter.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";

/**
 * @title Hyperlane Native Token Router that extends ERC20 with remote transfer functionality.
 * @author Abacus Works
 * @dev Supply on each chain is not constant but the aggregate supply across all chains is.
 * @dev This contract is the copy of base hyperlane HypNative.sol token with posibility to override specific functions in the child contracts.
 */
contract HypNative is FungibleTokenRouter {
    /**
     * @dev Emitted when native tokens are donated to the contract.
     * @param sender The address of the sender.
     * @param amount The amount of native tokens donated.
     */
    event Donation(address indexed sender, uint256 amount);

    constructor(uint256 _scale, address _mailbox) FungibleTokenRouter(_scale, _mailbox) {}

    /**
     * @notice Initializes the Hyperlane router
     * @param _hook The post-dispatch hook contract.
     *    @param _interchainSecurityModule The interchain security module contract.
     *    @param _owner The this contract.
     */
    function initialize(address _hook, address _interchainSecurityModule, address _owner) public initializer {
        _MailboxClient_initialize(_hook, _interchainSecurityModule, _owner);
    }

    /**
     * @inheritdoc TokenRouter
     * @dev uses (`msg.value` - `_amount`) as hook payment and `msg.sender` as refund address.
     */
    function transferRemote(uint32 _destination, bytes32 _recipient, uint256 _amount)
        external
        payable
        virtual
        override
        returns (bytes32 messageId)
    {
        require(msg.value >= _amount, "Native: amount exceeds msg.value");
        uint256 _hookPayment = msg.value - _amount;
        return _transferRemote(_destination, _recipient, _amount, _hookPayment);
    }

    /**
     * @inheritdoc TokenRouter
     * @dev uses (`msg.value` - `_amount`) as hook payment.
     */
    function transferRemote(
        uint32 _destination,
        bytes32 _recipient,
        uint256 _amount,
        bytes calldata _hookMetadata,
        address _hook
    ) external payable virtual override returns (bytes32 messageId) {
        require(msg.value >= _amount, "Native: amount exceeds msg.value");
        uint256 _hookPayment = msg.value - _amount;
        return _transferRemote(_destination, _recipient, _amount, _hookPayment, _hookMetadata, _hook);
    }

    function balanceOf(address _account) external view virtual override returns (uint256) {
        return _account.balance;
    }

    /**
     * @inheritdoc TokenRouter
     * @dev No-op because native amount is transferred in `msg.value`
     * @dev Compiler will not include this in the bytecode.
     */
    function _transferFromSender(uint256) internal view virtual override returns (bytes memory) {
        return bytes(""); // no metadata
    }

    /**
     * @dev Sends `_amount` of native token to `_recipient` balance.
     * @inheritdoc TokenRouter
     */
    function _transferTo(
        address _recipient,
        uint256 _amount,
        bytes calldata // no metadata
    ) internal virtual override {
        Address.sendValue(payable(_recipient), _amount);
    }

    receive() external payable {
        emit Donation(msg.sender, msg.value);
    }
}

